/*! Simple Ribbon Background */
(function(){
  var d=document,b=d.body; if(!b) return;
  var c=d.createElement('canvas'); c.id='ribbon-canvas'; c.style.cssText='position:fixed;top:0;left:0;z-index:-2;opacity:.5;'; b.appendChild(c);
  var w,h,ctx,pr=window.devicePixelRatio||1;
  function R(a){ return Math.random()*a; }
  function resize(){ w=window.innerWidth; h=window.innerHeight; c.width=w*pr; c.height=h*pr; c.style.width=w+'px'; c.style.height=h+'px'; ctx=c.getContext('2d'); ctx.scale(pr,pr); }
  function draw(){
    ctx.clearRect(0,0,w,h);
    var x=0,y=h*0.5,ax=200,ay=80, i=0;
    ctx.beginPath(); ctx.moveTo(x,y);
    while(x<w+200){
      x += ax * (0.4+R(0.6));
      y += ay * (R(2)-1);
      ctx.lineTo(x,y);
    }
    ctx.lineWidth = 60; ctx.strokeStyle='rgba(99,102,241,.12)'; ctx.stroke();
    setTimeout(draw, 3000);
  }
  resize(); draw();
  window.addEventListener('resize', resize);
})();